mapper.insert:
	INSERT INTO BOARD 
    VALUES (
			 BOARD_SEQ.NEXTVAL
		   , #{user_num}
		   , #{board_title}
		   , #{board_content}
		   , SYSDATE
		   , 0
		   , #{board_notice}
    	   )

mapper.update:
		UPDATE BOARD
  		   SET BOARD_TITLE = #{board_title}
     		 , BOARD_CONTENT = #{board_content}
     		 , BOARD_REGDATE = SYSDATE
 		 WHERE BOARD_NUM = #{board_num}

		UPDATE BOARD
  		   SET BOARD_READCOUNT = BOARD_READCOUNT+1 
		 WHERE BOARD_NUM = #{board_num}

mapper.select:
SELECT USER_NICK  
  FROM USER_INFO 
 WHERE USER_NUM = #{user_num}

SELECT BOARD_NUM 
  FROM BOARD_LIKE
 WHERE BOARD_NUM = #{board_num}
   AND USER_NUM = #{user_num}

SELECT COUNT(BOARD_NUM) 
  FROM BOARD_LIKE 
 WHERE BOARD_NUM = #{board_num}

mapper.delete:
DELETE
  FROM BOARD_LIKE 
 WHERE BOARD_NUM = #{board_num} 
   AND USER_NUM = #{user_num}

mapper.insert:
INSERT INTO BOARD_COMMENT
VALUES (
		 BOARD_COMMENT_SEQ.NEXTVAL
	   , #{board_num}
	   , #{user_num}
	   , #{comment_content}
	   , SYSDATE
	   , (
		   SELECT NVL(MAX(COMMENT_REFER)+1,0)
		     FROM BOARD_COMMENT
		 )
	   , 'N'	 		
	   )

INSERT INTO BOARD_COMMENT
VALUES (
		 BOARD_COMMENT_SEQ.NEXTVAL
	   , #{board_num}
	   , #{user_num}
	   , #{comment_content}
	   , SYSDATE
	   , (
		   SELECT COMMENT_REFER
		     FROM BOARD_COMMENT
		    WHERE COMMENT_NUM = #{comment_num}
		 )
	   , 'Y'
	   )

mapper.update:
		UPDATE BOARD_COMMENT
  		   SET COMMENT_CONTENT = #{comment_content}
     		 , COMMENT_REGDATE = SYSDATE
 		 WHERE COMMENT_NUM = #{comment_num}
