mapper.delete:
		DELETE FROM GOODS_KIND
 		 WHERE KIND_NUM IN
 			
			   #{kind_num}

mapper.sql:
SELECT CEIL(COUNT(*)/6)
  FROM GOODS
 WHERE KIND_NUM = #{kind_num}

mapper.insert:
	INSERT INTO GOODS
	VALUES (
	 		 GOODS_SEQ.NEXTVAL
   		   , #{goods_title}
   		   , #{user_num}
  		   , #{goods_doso}
  		   , #{goods_img_title}
  		   , #{goods_img_detail}
  		   , SYSDATE
  		   , 1
  		   , #{kind_num}
  		   , #{goods_history}
  		   , #{goods_cost}
   		   )

	INSERT INTO GOODS_OPTION
	VALUES (
			 GOODS_OPTION_SEQ.NEXTVAL
	
		
		   , (SELECT MAX(GOODS_NUM) FROM GOODS)
		
		
		   , #{goods_num}
		
	
  		   , #{option_name}
  		   , #{option_count}
  		   , #{option_weight}
  		   )

mapper.update:
		UPDATE GOODS
 		   SET GOODS_DELFLAG = 0
 		 WHERE GOODS_NUM IN
 			
			   #{goods_num}
			
		   AND KIND_NUM = #{kind_num}

		UPDATE GOODS
  		   SET GOODS_TITLE = #{goods_title}
			 , GOODS_DOSO = #{goods_doso}
		
			 , GOODS_IMG_TITLE = #{goods_img_title}
		
		
			 , GOODS_IMG_DETAIL = #{goods_img_detail}
			 
			 , GOODS_DATE = SYSDATE
	  		 , GOODS_DELFLAG = 1
			 , KIND_NUM = #{kind_num}
			 , GOODS_HISTORY = #{goods_history}
			 , GOODS_COST = #{goods_cost}
 		 WHERE GOODS_NUM = #{goods_num}

		UPDATE GOODS_OPTION
  		   SET OPTION_NAME = #{option_name}
			 , OPTION_COUNT = #{option_count}
			 , OPTION_WEIGHT = #{option_weight}
 		 WHERE OPTION_NUM = #{option_num}

mapper.delete:
		DELETE FROM BASKET
 		 WHERE OPTION_NUM IN
 			
			   #{option_num}

mapper.insert:
	INSERT INTO GOODS_ORDER
	VALUES (
	 		 GOODS_ORDER_SEQ.NEXTVAL
  		   , #{user_num}
   		   , #{order_seller}
  		   , #{goods_num}
		   , #{option_num}
		   , #{order_addr}
		   , #{order_money}
		   , SYSDATE
		   , SYSDATE+1
		   , 1
		   );

INSERT INTO USER_COUPON
VALUES (
	 	 USER_COUPON_SEQ.NEXTVAL
	   , (SELECT COUPON_NUM FROM COUPON WHERE COUPON_NUM = #{coupon_num})
	   , #{user_num}
	   ,  SYSDATE + #{coupon_period}
	   )
