mapper.delete:
		DELETE FROM GOODS_KIND
 		 WHERE KIND_NUM IN
 		 
			   #{kind_num}

mapper.insert:
	INSERT INTO GOODS
	VALUES (
	 		 GOODS_SEQ.NEXTVAL
   		   , #{goods_title}
   		   , #{user_num}
  		   , #{goods_doso}
  		   , #{goods_img_title}
  		   , SYSDATE
  		   , 'Y'
  		   , #{kind_num}
  		   , #{goods_history}
  		   , #{goods_cost}
   		   )

	INSERT INTO DETAIL_IMG
	VALUES (
			 DETAIL_IMG_SEQ.NEXTVAL
   		   , (SELECT MAX(GOODS_NUM) FROM GOODS)
  		   , #{detail_img_name}
  		   )

	INSERT INTO GOODS_OPTION
	VALUES (
			 GOODS_OPTION_SEQ.NEXTVAL
  		   , (SELECT MAX(GOODS_NUM) FROM GOODS)
  		   , #{option_name}
  		   , #{option_count}
  		   , #{option_weight}
  		   )
